import React, { useState } from "react";
import "./Signup.css";
import { useNavigate } from "react-router-dom";
import Navbar from "./Navbar";
import axios from "axios";
import config from "./config";
import Spinner from "./Spinner";

const Base_URL = config.baseURL;

function AdminSignup() {
  const navigate = useNavigate();

  const [adminId, setAdminId] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSignup = async (e) => {
    e.preventDefault();
    setLoading(true);

    const data = {
      adminId,
      username,
      password:'admin', // hardcoded as required
    };

    try {
      const res = await axios.post(`${Base_URL}/admin/signup`, data);

      if (res.status === 201) {
        alert("Admin registered successfully. Proceed to login.");
        navigate("/sign-in");
      } else {
        alert("Admin signup failed.");
      }
    } catch (error) {
      console.error("Admin signup error:", error.response?.data || error.message);
      if (error.response?.status === 409) {
        alert("Admin with this email already exists.");
      } else {
        alert("Something went wrong during admin signup.");
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <Navbar />
      <div className="card signup-card">
        <h2>Admin Sign Up</h2>
        <form onSubmit={handleSignup}>
          <div className="form-group">
            <label htmlFor="adminId">Admin ID:</label>
            <input
              type="text"
              id="adminId"
              value={adminId}
              onChange={(e) => setAdminId(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="username">Email (Username):</label>
            <input
              type="email"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label>Password:</label>
            <input type="text" value={password} 
             onChange={(e) => setPassword(e.target.value)}
             required
                          />
          </div>
          <button className="submit-button" type="submit" disabled={loading}>
            {loading ? <Spinner /> : "Register as Admin"}
          </button>
        </form>
      </div>
    </>
  );
}

export default AdminSignup;
